mapper.select:
SELECT count( * ) allNum
FROM ChimerDB3.ChimerKB 
WHERE 1=1
 ${queryForWebSource} 
 ${queryForBreakPointType} 
 ${queryForValidationMtd} 
 ${queryForFilterByFunc} 
 ${queryForFusType} 
 ${queryForSupInfo} ;

select distinct id, ChimerDB_Type, Source, webSource , Fusion_pair , if( instr(5Gene_Junction, "NA") > 0, if( substring_index(5Gene_Junction,":",-1) = "NA", "", substring_index(5Gene_Junction,":",-1) ) , 5Gene_Junction ) as gene5Junc , if( instr(3Gene_Junction, "NA") > 0, if( substring_index(3Gene_Junction,":",-1) = "NA", "", substring_index(3Gene_Junction,":",-1) ) , 3Gene_Junction ) as gene3Junc , H_gene , if(H_chr = "NA","",H_chr) as H_chr , H_position , if(H_strand = "NA","",H_strand) as H_strand , T_gene , if(T_chr = "NA","",T_chr) as T_chr , T_position , if(T_strand = "NA","",T_strand) as T_strand , Genomic_breakpoint, Exon_breakpoint , if(Breakpoint_Type = "NA", "", Breakpoint_Type) as Breakpoint_Type , Genome_Build_Version , if(PMID = "NA","",PMID) as PMID , if(Disease = "NA","",Disease) as Disease , if(Validation = "NA","", Validation) as Validation , if(Frame = "NA","",Frame) as Frame , Chr_info, Kinase, Oncogene, Tumor_suppressor, Receptor , Transcription_Factor, ChimerSeq, ChimerPub from ChimerDB3.ChimerKB where 1=1
and Fusion_pair = "${Fusion_pair}" and Breakpoint_Type = "${Breakpoint_Type}"
limit 1;

select distinct(Disease) lst from ChimerDB3.ChimerKB
where Disease != "NA"

    
        and Disease like "%${_parameter}%" ;
    
    
        limit 10;
