mapper.select:
SELECT
	@rownum:=@rownum+1 AS elementIndex
	, a.*
FROM (
SELECT a.* FROM I1_GRCh37_GFF a, (
	SELECT max(no) no FROM (
		SELECT a.* FROM I1_Gene_Info a, (
			SELECT gene_id FROM I1_GeneSymbol_Synonym
			WHERE MATCH(symbol) AGAINST(#{symbol} IN BOOLEAN MODE)
			AND symbol = #{symbol}
		) b
		WHERE a.gene_id=b.gene_id
	) a LEFT JOIN I1_GRCh37_GFF b
	ON b.Gene_ID=a.gene_id
	AND b.no BETWEEN a.gff_start AND a.gff_end
	AND b.type IN ('exon')
	GROUP BY start, end
) b
WHERE a.no=b.no
) a, (SELECT @rownum:=0) b

SELECT text FROM I2_AutocompleteTable
WHERE service=#{service} AND type=${type}
AND MATCH(text) AGAINST('${text}*' IN BOOLEAN MODE)

SELECT Fusion_pair AS fusion_pair , IF(5Gene_Junction='NA', '', 5Gene_Junction) AS gene5Junc , IF(3Gene_Junction='NA', '', 3Gene_Junction) AS gene3Junc , IF(Breakpoint_Type='NA', '', Breakpoint_Type) AS breakpoint_type , IF(Cancertype='NA', '', Cancertype) AS cancertype , IF(BarcodeID='NA', '', BarcodeID) AS barcodeID , IF(Frame = "NA","",Frame) AS frame , IF(Chr_info='NA', '', Chr_info) AS chr_info , IF(Source='NA', '', Source) AS source , IF(ChimerKB='NA', '', ChimerKB) AS chimerKB , IF(ChimerPub='NA', '', ChimerPub) AS chimerPub , CONCAT(ChimerKB, "_", ChimerPub) AS supported , IF(T_gene='NA', '', T_gene) AS t_gene , IF(H_gene='NA', '', H_gene) AS h_gene
, IF( ChimerDB_Type='NA', '', ChimerDB_Type ) AS chimerDB_Type , IF( webSource='NA', '', webSource ) AS webSource , IF( H_chr='NA', '', H_chr ) AS h_chr , IF( H_position='NA', '', H_position ) AS h_position , IF( H_strand='NA', '', H_strand ) AS h_strand , IF( T_position='NA', '', T_position ) AS t_position , IF( T_chr='NA', '', T_chr ) AS t_chr , IF( T_strand='NA', '', T_strand ) AS t_strand , IF( Genomic_breakpoint='NA', '', Genomic_breakpoint ) AS genomic_breakpoint , IF( Exon_breakpoint='NA', '', Exon_breakpoint ) AS exon_breakpoint , IF( Genome_Build_Version='NA', '', Genome_Build_Version ) AS genome_Build_Version
, IF( Seed_reads_num='NA', '', Seed_reads_num ) AS seed_reads_num , IF( Spanning_pairs_num='NA', '', Spanning_pairs_num ) AS spanning_pairs_num , IF( Junction_reads_num='NA', '', Junction_reads_num ) AS junction_reads_num , IF( H_locus='NA', '', H_locus ) AS h_locus , IF( T_locus='NA', '', T_locus ) AS t_locus , IF( Kinase='NA', '', Kinase ) AS kinase , IF( Tumor_suppressor='NA', '', Tumor_suppressor ) AS tumor_suppressor , IF( Receptor='NA', '', Receptor ) AS receptor , IF( Oncogene='NA', '', Oncogene ) AS oncogene , IF( Transcription_Factor='NA', '', Transcription_Factor ) AS transcription_Factor , IF( Disease='NA', '', Disease ) AS disease FROM ChimerDB3.ChimerSeq WHERE id=${id}
